Emerald Woods 0.4.2html,
		body {
			margin: 0;
		}

		body {
			background-color: #060608;
			font-family: sans-serif;
			color: #24142c;
		}

		.centerer {
			margin-top: 0;
			margin-left: auto;
			margin-right: auto;
			text-align: center;
		}

		#game {
			background-color: #060608;
		}-

@charset "utf-8";

@font-face {
	font-family: 'DejaVuSansMono_ut';
	src: url('DejaVuSansMono.eot');
	src: url('DejaVuSansMono.eot?#iefix') format('eot'),
		url('DejaVuSansMono.woff') format('woff'),
		url('DejaVuSansMono.ttf') format('truetype');
}

.unicodetiles {
	font-family: "DejaVuSansMono_ut";
	font-size: 24pt;
	white-space: pre;
	text-align: center;
	line-height: 1;
	letter-spacing: 0px;
	display: inline-block;
}

.unicodetiles div {
	float: left;
	height: 1em;
}

.unicodetiles br {
	clear: both;
}

